function rowLevels(rows, nlevs) {
    var ylevs = nlevs + 1;
    var xlevs = nlevs + 1;
    var tlevs = xlevs;
    var mlevs;
    for (row of rows) {
        if (row.getTreeChildren().length > 0) {
            mlevs = rowLevels(row.getTreeChildren(), xlevs);
            tlevs = Math.max(tlevs, mlevs);
        }
        else {
            tlevs = Math.max(tlevs, xlevs);
        }
    }

    return tlevs;
}

